function RespContacto(result) {

    var obj = jQuery.parseJSON(result);

    if (obj.Ok == true) {
        NotificContacto("Suc", "OK. (<b>Contacto Realizado Satisfactoriamente!.</b>)");
    }
    else {
        NotificContacto("Dan", "Error. (<b>" + obj.Mensaje + "</b>)");
    }
    console.log(obj);
}

function ValidarCorreo(idname) {
    var txt = document.forms[0].elements[idname].value;
    var b = /^[^@\s]+@[^@\.\s]+(\.[^@\.\s]+)+$/;
    if (b.test(txt)) {
        return true;
    }
    else {
        document.forms[0].elements[idname].value = "";
        document.forms[0].elements[idname].setfocus;
        return false;
    }
}
