function NotificContacto(Tipo,Mensaje) {

    var nFrom = "top";// $(this).attr('data-from');
    var nAlign = "center"; // $(this).attr('data-align');
    var nIcons = "fa fa-comments"; //$(this).attr('data-icon');
    var nType = "info";//$(this).attr('data-type');

    var nAnimIn = "animated fadeInDown"; // $(this).attr('data-animation-in');
    var nAnimOut = "animated fadeOutDown";  //$(this).attr('data-animation-out');

    /*==============================================*/ 
    if (Tipo == "Dan") {
        nType = "danger";

        nAnimIn = "animated fadeInRight";
        nAnimOut = "animated fadeOutRight";
    }
  
    if (Tipo == "Suc") {
        nType = "success";

        nAnimIn = "animated fadeInDown";
        nAnimOut = "animated fadeOutDown";
    }
 /*==============================================*/
  


    notify(nFrom, nAlign, nIcons, nType, nAnimIn, nAnimOut, Mensaje);

}
